action.data:
connection.acquire();

var numChannels = parseInt(await connection.query("SYSTem:CHANnel?"));


if (numChannels === 0) {
    connection.release();
    notify.error("There is no channel installed!");
    return;
}

var defaultValues = storage.getItem("EezDlogValues", {
    ch1voltage: true,
    ch1current: true,
    ch1power: false,
    ch2voltage: true,
    ch2current: true,
    ch2power: false,
    ch3voltage: false,
    ch3current: false,
    ch3power: false,
    ch4voltage: false,
    ch4current: false,
    ch4power: false,
    ch5voltage: false,
    ch5current: false,
    ch5power: false,
    ch6voltage: false,
    ch6current: false,
    ch6power: false,
    period: 0.02,
    time: 10,
    file: "file.dlog"
});

var fields = [];

for (let i = 1; i <= numChannels; i++) {
    fields = fields.concat([
        { 
            name: `ch${i}voltage`,
            displayName: `Log channel ${i} voltage`,
            type: "boolean"
        },
        { 
            name: `ch${i}current`,
            displayName: `Log channel ${i} current`,
            type: "boolean"
        },
        { 
            name: `ch${i}power`,
            displayName: `Log channel ${i} power`,
            type: "boolean"
        },
    ]);
}

fields = fields.concat([
    { 
        name: "period",
        unit: "time",
        validators: [validators.rangeInclusive(0.005, 120)]
    },
    { 
        name: "time",
        unit: "time",
        validators: [validators.rangeInclusive(1, 86400000)]
    },
    { 
        name: "file",
        type: "string",
        validators: [validators.required]
    }
]);

var values = await input({
    title: "Start Dlog",
    fields
}, defaultValues);

if (!values) {
    connection.release();
    session.deleteScriptLogEntry();
    return;
}

storage.setItem("EezDlogValues", values);

session.scriptParameters = values;

for (let i = 1; i <= numChannels; i++) {
    connection.command(`SENS:DLOG:FUNC:VOLT ${values[`ch${i}voltage`] ? "ON" : "OFF"}, ch${i}`);
    connection.command(`SENS:DLOG:FUNC:CURR ${values[`ch${i}current`] ? "ON" : "OFF"}, ch${i}`);
    connection.command(`SENS:DLOG:FUNC:POW ${values[`ch${i}power`] ? "ON" : "OFF"}, ch${i}`);
}

connection.command(`SENS:DLOG:PER ${values.period}`);
connection.command(`SENS:DLOG:TIME ${values.time}`);
connection.command("TRIG:DLOG:SOUR IMM");
connection.command(`INIT:DLOG "${values.file}"`);

connection.release();

var defaultValues = storage.getItem("EezDlogValues", {
    file: "file.dlog"
});

var values = await input({
    title: "Upload Dlog",
    fields: [
        { 
            name: "file",
            type: "string"
        }
    ]
}, {
    file: defaultValues.file
});

if (!values) {
    session.deleteScriptLogEntry();
    return;
}

session.scriptParameters = values;

connection.acquire();

var file = await connection.query(`MMEMory:UPLoad? "${values.file}"`);
if (!file.transferSucceeded) {
    session.scriptError = "Upload failed.";
}

connection.release();

